@use '../pages/theme/theme';

.top-banner {
  .title {
    align-self: center;
    margin: 0;
  }
}

#todolist {
  width: 100%;

  .todo {
    width: 100%;
    padding: 4px;
    // height: 30px;

    label {
      margin: 0px;
      margin-right: 8px;
      margin-left: 12px;
      font-size: 12px;
      color: theme.$color-gray;
      font-style: italic;
    }

    .main-container {
      width: 100%;
      display: flex;
      flex-direction: row;
      justify-content: space-between;

      .content-container {
        width: 100%;
        .content {
          width: 100%;
          margin-left: 8px;
          font-size: 18px;
          font-weight: 500;
        }
      }

      .content.done {
        text-decoration: line-through;
        color: theme.$color-gray;
      }

      .unactive {
        display: none;
      }

      .action-buttons {
        display: flex;

        .text-button {
          font-size: 18px;
          min-width: 0;
          color: theme.$color-gray;
        }

        .text-button:hover {
          color: theme.$color-white;
        }
      }
    }

    .child-container {
      width: 100%;
      .child-input-container {
        width: 100%;
        display: flex;
        flex-direction: row;

        .icon {
          font-size: 12px;
          padding-top: 10px;
          padding-right: 4px;
        }

        input {
          width: 100%;
          padding: 4px;
          padding-left: 8px;
        }
      }
      .child-todo-list {
        width: auto;
        margin-left: 32px;

        .divider {
          display: none;
        }
      }
    }

    .unactive {
      display: none;
    }

    .divider {
      margin-top: 4px;
      width: 98%;
      margin-left: auto;
      margin-right: auto;
      border-bottom: solid theme.$color-gray 0.3px;
    }
  }
}

#content::placeholder {
  font-style: italic;
}

